mapper.insert:
/* [ProductMngMapper - mergeProductImg] 제품 이미지 저장 */
insert into product_img (
	prdt_seq,
	prdt_img
) values (
	#{prdt_seq},
	#{prdt_img}
)
on duplicate key update
	prdt_img	= #{prdt_img}

mapper.select:
/* [ProductMngMapper - getProductList] 제품 목록 조회 */ select t.* from( select @ROWNUM:=@ROWNUM+1 as row_num, a.* from (select pm.prdt_seq, pm.prdt_nm, pm.prdt_price, pm.prdt_sale_price, pm.prdt_img, pm.prdt_stock, pm.prdt_season, pm.prdt_dtl, pm.prdt_info, pm.korea_shipping_yn, pm.intl_shipping_yn, pm.reg_dtm, pm.reg_uid, pm.mdf_dtm, pm.mdf_uid from product_mng pm
inner join product_cate pc on pm.prdt_seq = pc.prdt_seq
inner join product_character pch on pm.prdt_seq = pch.prdt_seq
, (select @ROWNUM:=0) r where 1=1
and pm.prdt_seq = ${prdt_seq}
and pc.cate_seq = ${sch_cate_seq}
and pch.char_seq = ${sch_char_seq}
order by prdt_seq asc )a )t
where row_num between #{first_idx} and #{last_idx}

/* [ProductMngMapper - getProduct] 제품 단건 조회 */ select pm.prdt_seq, pm.prdt_nm, pm.prdt_price, pm.prdt_sale_price, pm.prdt_img, pm.prdt_stock, pm.prdt_season, pm.prdt_dtl, pm.prdt_info, pm.korea_shipping_yn, pm.intl_shipping_yn,
(select count(1) from order_mng om inner join order_detail od on om.ord_num = od.ord_num where om.ord_uid = ${uid} and od.prdt_seq = ${prdt_seq}) as ord_cnt,
pm.reg_dtm, pm.reg_uid, pm.mdf_dtm, pm.mdf_uid from product_mng pm where 1=1
and pm.prdt_seq = ${prdt_seq}
order by prdt_seq asc
